var CWD = process.cwd();

var path = require('path');

var fs = require('fs');

var glob = require('glob');

var program = require('commander');

var metadataUtils = require('./metadataUtils');

var env = require('./env.js');

var blog = require('./blog.js');

var loadConfig = require('./config');

var siteConfig = loadConfig("".concat(CWD, "/siteConfig.js"));

var versionFallback = require('./versionFallback.js');

var utils = require('./utils.js');

var docsPart = "".concat(siteConfig.docsUrl ? "".concat(siteConfig.docsUrl, "/") : '');
var SupportedHeaderFields = new Set(['id', 'title', 'author', 'authorURL', 'authorFBID', 'sidebar_label', 'original_id', 'hide_title', 'layout', 'custom_edit_url', 'description']);
var allSidebars;

if (fs.existsSync("".concat(CWD, "/sidebars.json"))) {
  allSidebars = require("".concat(CWD, "/sidebars.json"));
} else {
  allSidebars = {};
} // Can have a custom docs path. Top level folder still needs to be in directory
// at the same level as `website`, not inside `website`.
//   e.g., docs/whereDocsReallyExist
//         website-docs/
// All .md docs still (currently) must be in one flat directory hierarchy.
//   e.g., docs/whereDocsReallyExist/*.md (all .md files in this dir)


function getDocsPath() {
  return siteConfig.customDocsPath ? siteConfig.customDocsPath : 'docs';
}

function shouldGenerateNextReleaseDocs() {
  return !(env.versioning.enabled && program.skipNextRelease);
} // returns map from id to object containing sidebar ordering info


function readSidebar() {
  var sidebars = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : {};
  Object.assign(sidebars, versionFallback.sidebarData());
  var items = {};
  Object.keys(sidebars).forEach(function (sidebar) {
    var categories = sidebars[sidebar];
    var sidebarItems = [];
    Object.keys(categories).forEach(function (category) {
      var categoryItems = categories[category];
      categoryItems.forEach(function (categoryItem) {
        if (_typeof(categoryItem) === 'object') {
          switch (categoryItem.type) {
            case 'subcategory':
              categoryItem.ids.forEach(function (subcategoryItem) {
                sidebarItems.push({
                  id: subcategoryItem,
                  category: category,
                  subcategory: categoryItem.label,
                  order: sidebarItems.length + 1
                });
              });
              return;

            default:
              return;
          }
        } // Is a regular id value.


        sidebarItems.push({
          id: categoryItem,
          category: category,
          subcategory: null,
          order: sidebarItems.length + 1
        });
      });
    });

    for (var i = 0; i < sidebarItems.length; i++) {
      var item = sidebarItems[i];
      var previous = null;
      var next = null;

      if (i > 0) {
        previous = sidebarItems[i - 1].id;
      }

      if (i < sidebarItems.length - 1) {
        next = sidebarItems[i + 1].id;
      }

      items[item.id] = {
        previous: previous,
        next: next,
        sidebar: sidebar,
        category: item.category,
        subcategory: item.subcategory,
        order: item.order
      };
    }
  });
  return items;
} // process the metadata for a document found in either 'docs' or 'translated_docs'


function processMetadata(file, refDir) {
  var result = metadataUtils.extractMetadata(fs.readFileSync(file, 'utf8'));
  var language = utils.getLanguage(file, refDir) || 'en';
  var metadata = {};
  Object.keys(result.metadata).forEach(function (fieldName) {
    if (SupportedHeaderFields.has(fieldName)) {
      metadata[fieldName] = result.metadata[fieldName];
    } else {
      console.warn("Header field \"".concat(fieldName, "\" in ").concat(file, " is not supported."));
    }
  });
  var rawContent = result.rawContent;

  if (!metadata.id) {
    metadata.id = path.parse(file).name;
  }

  if (metadata.id.includes('/')) {
    throw new Error('Document id cannot include "/".');
  } // If a file is located in a subdirectory, prepend the subdir to it's ID
  // Example:
  //  (file: 'docusaurus/docs/projectA/test.md', ID 'test', refDir: 'docusaurus/docs')
  //  returns 'projectA/test'


  var subDir = utils.getSubDir(file, refDir);

  if (subDir) {
    metadata.id = "".concat(subDir, "/").concat(metadata.id);
  } // Example: `docs/projectA/test.md` source is `projectA/test.md`


  metadata.source = subDir ? "".concat(subDir, "/").concat(path.basename(file)) : path.basename(file);

  if (!metadata.title) {
    metadata.title = metadata.id;
  }

  var langPart = env.translation.enabled || siteConfig.useEnglishUrl ? "".concat(language, "/") : '';
  var versionPart = '';

  if (env.versioning.enabled) {
    metadata.version = 'next';
    versionPart = 'next/';
  }

  metadata.permalink = "".concat(docsPart).concat(langPart).concat(versionPart).concat(metadata.id, ".html"); // change ids previous, next

  metadata.localized_id = metadata.id;
  metadata.id = (env.translation.enabled ? "".concat(language, "-") : '') + metadata.id;
  metadata.language = env.translation.enabled ? language : 'en';
  var items = readSidebar(allSidebars);
  var id = metadata.localized_id;
  var item = items[id];

  if (item) {
    metadata.sidebar = item.sidebar;
    metadata.category = item.category;
    metadata.subcategory = item.subcategory;
    metadata.order = item.order;

    if (item.next) {
      metadata.next_id = item.next;
      metadata.next = (env.translation.enabled ? "".concat(language, "-") : '') + item.next;
    }

    if (item.previous) {
      metadata.previous_id = item.previous;
      metadata.previous = (env.translation.enabled ? "".concat(language, "-") : '') + item.previous;
    }
  }

  return {
    metadata: metadata,
    rawContent: rawContent
  };
} // process metadata for all docs and save into core/metadata.js


function generateMetadataDocs() {
  var order;

  try {
    order = readSidebar(allSidebars);
  } catch (e) {
    console.error(e);
    process.exit(1);
  }

  var enabledLanguages = env.translation.enabledLanguages().map(function (language) {
    return language.tag;
  });
  var metadatas = {};
  var defaultMetadatas = {};

  if (shouldGenerateNextReleaseDocs()) {
    // metadata for english files
    var docsDir = path.join(CWD, '../', getDocsPath());
    var files = glob.sync("".concat(docsDir, "/**"));
    files.forEach(function (file) {
