var CWD = process.cwd();

var glob = require('glob');

var fs = require('fs');

var path = require('path');

var metadataUtils = require('./metadataUtils');

var env = require('./env.js');

var utils = require('./utils.js');

var loadConfig = require('./config');

var siteConfig = loadConfig("".concat(CWD, "/siteConfig.js"));
var ENABLE_TRANSLATION = fs.existsSync("".concat(CWD, "/languages.js"));
var versions;

if (fs.existsSync("".concat(CWD, "/versions.json"))) {
  versions = require("".concat(CWD, "/versions.json"));
} else {
  versions = [];
}

var languages;

if (fs.existsSync("".concat(CWD, "/languages.js"))) {
  languages = require("".concat(CWD, "/languages.js"));
} else {
  languages = [{
    enabled: true,
    name: 'English',
    tag: 'en'
  }];
}

var versionFolder = "".concat(CWD, "/versioned_docs/"); // available stores doc ids of documents that are available for
// each version

var available = {}; // versionFiles is used to keep track of what file to use with a
// given version/id of a document

var versionFiles = {};
var files = glob.sync("".concat(versionFolder, "**"));
files.forEach(function (file) {
  var ext = path.extname(file);

  if (ext !== '.md' && ext !== '.markdown') {
    return;
  }

  var res = metadataUtils.extractMetadata(fs.readFileSync(file, 'utf8'));
  var metadata = res.metadata;

  if (!metadata.original_id) {
    console.error("No 'original_id' field found in ".concat(file, ". Perhaps you forgot to add it when importing prior versions of your docs?"));
    throw new Error("No 'original_id' field found in ".concat(file, ". Perhaps you forgot to add it when importing prior versions of your docs?"));
  }

  if (!metadata.id) {
    console.error("No 'id' field found in ".concat(file, "."));
    throw new Error("No 'id' field found in ".concat(file, "."));
  } else if (metadata.id.indexOf('version-') === -1) {
    console.error("The 'id' field in ".concat(file, " is missing the expected 'version-XX-' prefix. Perhaps you forgot to add it when importing prior versions of your docs?"));
    throw new Error("The 'id' field in ".concat(file, " is missing the expected 'version-XX-' prefix. Perhaps you forgot to add it when importing prior versions of your docs?"));
  } // The version will be between "version-" and "-<metadata.original_id>"
  // e.g. version-1.0.0-beta.2-doc1 => 1.0.0-beta.2
  // e.g. version-1.0.0-doc2 => 1.0.0
  // e.g. version-1.0.0-getting-started => 1.0.0


  var version = metadata.id.substring(metadata.id.indexOf('version-') + 8, // version- is 8 characters
  metadata.id.lastIndexOf("-".concat(metadata.original_id))); // the original_id should be namespaced according to subdir to allow duplicate id in different subfolder

  var subDir = utils.getSubDir(file, path.join(versionFolder, "version-".concat(version)));

  if (subDir) {
    metadata.original_id = "".concat(subDir, "/").concat(metadata.original_id);
  }

  if (!(metadata.original_id in available)) {
    available[metadata.original_id] = new Set();
  }

  available[metadata.original_id].add(version);

  if (!(version in versionFiles)) {
    versionFiles[version] = {};
  }

  versionFiles[version][metadata.original_id] = file;
}); // returns the version to use for a document based on its id and
// what the requested version is

function docVersion(id, reqVersion) {
  if (!available[id]) {
    return null;
  } // iterate through versions until a version less than or equal to the requested
  // is found, then check if that version has an available file to use


  var requestedFound = false;

  for (var i = 0; i < versions.length; i++) {
    if (versions[i] === reqVersion) {
      requestedFound = true;
    }

    if (requestedFound && available[id].has(versions[i])) {
      return versions[i];
    }
  }

  return null;
} // returns whether a given file has content that differ from the
// document with the given id


function diffLatestDoc(file, id) {
  if (versions.length === 0) {
    return true;
  }

  var latest = versions[0];
  var version;

  try {
    version = docVersion(id, latest);
  } catch (e) {
    console.error(e);
    process.exit(1);
  }

  if (!version) {
    return true;
  }

  var latestFile = versionFiles[version][id];

  if (!latestFile || !fs.existsSync(latestFile)) {
    return true;
  }

  return metadataUtils.extractMetadata(fs.readFileSync(latestFile, 'utf8')).rawContent.trim() !== metadataUtils.extractMetadata(fs.readFileSync(file, 'utf8')).rawContent.trim();
} // return metadata for a versioned file given the file, its version (requested),
// the version of the file to be used, and its language


function processVersionMetadata(file, version, useVersion, language) {
  var metadata = metadataUtils.extractMetadata(fs.readFileSync(file, 'utf8')).metadata; // Add subdirectory information to versioned_doc metadata
  // Example: `versioned_docs/version-1.1.6/projectA/readme.md` file with id `version-1.1.6-readme`
  // and original_id `readme` will have metadata id of `version-1.1.6-projectA/readme` and original_id `projectA/readme`

  var subDir = utils.getSubDir(file, path.join(CWD, 'versioned_docs', "version-".concat(useVersion)));

  if (subDir) {
    metadata.original_id = "".concat(subDir, "/").concat(metadata.original_id);
    metadata.id = metadata.id.replace("version-".concat(useVersion, "-"), "version-".concat(useVersion, "-").concat(subDir, "/"));
  }

  metadata.source = subDir ? "version-".concat(useVersion, "/").concat(subDir, "/").concat(path.basename(file)) : "version-".concat(useVersion, "/").concat(path.basename(file));
  var latestVersion = versions[0];
  var docsPart = "".concat(siteConfig.docsUrl ? "".concat(siteConfig.docsUrl, "/") : '');
  var versionPart = "".concat(version !== latestVersion ? "".concat(version, "/") : '');

  if (!ENABLE_TRANSLATION && !siteConfig.useEnglishUrl) {
    metadata.permalink = "".concat(docsPart).concat(versionPart).concat(metadata.original_id, ".html");
  } else {
    metadata.permalink = "".concat(docsPart).concat(language, "/").concat(versionPart).concat(metadata.original_id, ".html");
  }

  metadata.id = metadata.id.replace("version-".concat(useVersion, "-"), "version-".concat(version, "-"));
  metadata.localized_id = metadata.id;
  metadata.id = (env.translation.enabled ? "".concat(language, "-") : '') + metadata.id;
  metadata.language = language;
  metadata.version = version;
  return metadata;
} // return all metadata of versioned documents


function docData() {
  var allIds = new Set();
  Object.keys(versionFiles).forEach(function (version) {
    Object.keys(versionFiles[version]).forEach(function (id) {
      allIds.add(id);
    });
  });
  var metadatas = [];
  languages.filter(function (language) {
    return language.enabled;
  }).forEach(function (language) {
    versions.forEach(function (version) {
      allIds.forEach(function (id) {
        var useVersion;

        try {
          useVersion = docVersion(id, version);
        } catch (e) {
          console.log(e);
          process.exit(1);
